const suma = (a, b) => {
  return a + b;
};

const resta = (a, b) => {
  return a - b;
};

const multiplicacion = (a, b) => {
  return a * b;
};

const division = (a, b) => {
  return a / b;
};

const residuo = (a, b) => {
  return a % b;
};

const potencia = (a, b) => {
  return a ** b;
};
export default {
  suma,
  resta,
  multiplicacion,
  division,
  residuo,
  potencia,
};
